let _ = require('lodash'),
    rand = require('app/utils/rand'),
    config = require('app/config');

module.exports = (lines) =>
  _
  .range(config.numberOfBuses)
  .map(
    (item, index) => {
      return {
        id: index,
        line: lines[
          rand(lines.length)
        ],
        location: rand.random(),
        capacity: config.busCapacity
      };
    }
  );

