let _ = require('lodash'),
    rand = require('app/utils/rand'),
    stopNames = require('app/fixtures/stopNames'),
    config = require('app/config');

module.exports = () =>
  _.range(config.stops).map(
    (item, index) => {
      return {
        id: index,
        name: stopNames[index],
        pos: {
          x: rand(config.gridSize),
          y: rand(config.gridSize)
        }
      };
    }).sort(
      (a, b) =>
        a.pos.x - b.pos.x || a.pos.y - b.pos.y
    );
