module.exports = function *corsFix(next) {
  this.set(
    'Access-Control-Allow-Origin',
    '*'
  );
  this.set(
    'Access-Control-Allow-Methods',
    [
      'GET',
      'OPTIONS',
      'HEAD',
      'PUT',
      'POST',
      'DELETE',
      'PATCH'
    ].join(',')
  );
  this.set(
    'Access-Control-Allow-Headers',
    [
      'origin',
      'x-http-method-override',
      'accept',
      'content-type',
      'authorization',
      'x-pingother'
    ].join(',')
  );
  yield next;
};
