let _ = require('lodash'),
    rand = require('app/utils/rand'),
    data = {};

data.stops = require('app/factories/stops')();
data.lines = require('app/factories/lines')(data.stops);
data.buses = require('app/factories/buses')(data.lines);

setInterval(function () {
  // data.buses = data.buses.map(function (bus) {
  // });
  data.lines = data.lines.map(line => {
    line.latencies = line.latencies.map(latency => latency - Math.random() + Math.random());
    return line;
  });
}, 1000);

module.exports = {
  data: data
};
