module.exports = (begin, end) => {
  let buffer = [],
      pos = {
        x: begin.x,
        y: begin.y
      };

  while (pos.x !== end.x) {
    pos = {
      x: pos.x + (pos.x < end.x ? 1 : -1),
      y: pos.y
    };
    buffer.push(pos);
  }

  while (pos.y !== end.y) {
    pos = {
      x: pos.x,
      y: pos.y + (pos.y < end.y ? 1 : -1)
    };
    buffer.push(pos);
  }

  return buffer;
};
